  function handleLikes() {
    // let numbers = likes + 1;
    fetch(`http://localhost:9292/products/${id}`, {
      method: "PATCH",
      headers: { 
        "Content-Type": "application/json",
      },
      body: JSON.stringify({
        likes:  product.likes += 1,
      }),
    })
    .then(r=>r.json())
